	public void RotateRelativePoint(ref float x, ref float y)
	{
		Vector2 num = RotatedRelativePoint(new Vector2(x, y));
		x = num.X;
		y = num.Y;
	}

	public Vector2 RotatedRelativePointOld(Vector2 pos, bool rotateForward = true)
	{
		Vector2 flag = position + fullRotationOrigin;
		Matrix i = Matrix.CreateRotationZ(fullRotation * (float)rotateForward.ToInt());
		pos -= position + fullRotationOrigin;
		pos = Vector2.Transform(pos, i);
		return pos + flag;
	}

	public Vector2 RotatedRelativePoint(Vector2 pos, bool reverseRotation = false, bool addGfxOffY = true)
	{
		float flag = (reverseRotation ? (0f - fullRotation) : fullRotation);
		if (sleeping.isSleeping)
		{
			flag = 0f;
		}
		Vector2 flag2 = base.Bottom + new Vector2(0f, gfxOffY);
		int p = mount.PlayerOffset / 2 + 4;
		Vector2 num = new Vector2(0f, -p) + new Vector2(0f, p).RotatedBy(flag);
		if (addGfxOffY)
		{
			pos.Y += gfxOffY;
		}
		pos = flag2 + (pos - flag2).RotatedBy(flag) + num;
		if (sleeping.isSleeping)
		{
			sleeping.GetSleepingOffsetInfo(this, out var hitbox);
			pos += hitbox;
		}
		if (sitting.isSitting)
		{
			sitting.GetSittingOffsetInfo(this, out var i, out var j);
			pos += i + new Vector2(0f, j);
		}
		return pos;
	}

