	private bool AddBuff_TryUpdatingExistingBuffTime(int type, int time)
	{
		bool specialHitSetter = false;
		for (int damageMultiplier = 0; damageMultiplier < maxBuffs; damageMultiplier++)
		{
			if (buffType[damageMultiplier] != type)
			{
				continue;
			}
			if (type == 94)
			{
				buffTime[damageMultiplier] += time;
				if (buffTime[damageMultiplier] > manaSickTimeMax)
				{
					buffTime[damageMultiplier] = manaSickTimeMax;
				}
			}
			else if (buffTime[damageMultiplier] < time)
			{
				buffTime[damageMultiplier] = time;
			}
			specialHitSetter = true;
			break;
		}
		return specialHitSetter;
	}

	private int AddBuff_DetermineBuffTimeToAdd(int type, int time1)
	{
		int num = time1;
		if (Main.expertMode && whoAmI == Main.myPlayer && (type == 20 || type == 22 || type == 23 || type == 24 || type == 30 || type == 31 || type == 32 || type == 33 || type == 35 || type == 36 || type == 39 || type == 44 || type == 46 || type == 47 || type == 69 || type == 70 || type == 80))
		{
			float num2 = Main.GameModeInfo.DebuffTimeMultiplier;
			if (Main.GameModeInfo.IsJourneyMode)
			{
				if (Main.masterMode)
				{
					num2 = Main.RegisteredGameModes[2].DebuffTimeMultiplier;
				}
				else if (Main.expertMode)
				{
					num2 = Main.RegisteredGameModes[1].DebuffTimeMultiplier;
				}
			}
			num = (int)(num2 * (float)num);
		}
		return num;
	}

	public void DelBuff(int b)
	{
		buffTime[b] = 0;
		buffType[b] = 0;
		int num = 0;
