			}
		}
		else if (controlDown)
		{
			if (velocity.Y < 0f)
			{
				velocity.Y *= 0.9f;
			}
			velocity.Y += flag2;
			if (velocity.Y > flag)
			{
				velocity.Y = flag;
			}
		}
		else if ((double)velocity.Y < -0.1 || (double)velocity.Y > 0.1)
		{
			velocity.Y *= 0.9f;
		}
		else
		{
			velocity.Y = 0f;
		}
		if (controlLeft && !controlRight)
		{
			if (velocity.X > 0f)
			{
				velocity.X *= 0.9f;
			}
			velocity.X -= flag2;
			if (velocity.X < 0f - flag)
			{
				velocity.X = 0f - flag;
			}
		}
		else if (controlRight && !controlLeft)
		{
			if (velocity.X < 0f)
			{
				velocity.X *= 0.9f;
			}
			velocity.X += flag2;
			if (velocity.X > flag)
			{
				velocity.X = flag;
			}
		}
		else if (velocity.X < 0f - flag2 || velocity.X > flag2)
		{
			velocity.X *= 0.9f;
		}
		else
		{
			velocity.X = 0f;
		}
		position += velocity;
		ghostFrameCounter++;
		if (velocity.X < 0f)
		{
			direction = -1;
		}
		else if (velocity.X > 0f)
		{
			direction = 1;
		}
