	public void UpdateImmunity()
	{
		if (immune)
		{
			immuneTime--;
			if (immuneTime <= 0)
			{
				immune = false;
				immuneNoBlink = false;
			}
			if (immuneNoBlink)
			{
				immuneAlpha = 0;
			}
			else
			{
				immuneAlpha += immuneAlphaDirection * 50;
				if (immuneAlpha <= 50)
				{
					immuneAlphaDirection = 1;
				}
				else if (immuneAlpha >= 205)
				{
					immuneAlphaDirection = -1;
				}
			}
		}
		else
		{
			immuneAlpha = 0;
		}
		for (int vector = 0; vector < hurtCooldowns.Length; vector++)
		{
			if (hurtCooldowns[vector] > 0)
			{
				hurtCooldowns[vector]--;
			}
		}
	}

	private void TryToPoop()
	{
		if (whoAmI != Main.myPlayer || !wellFed)
		{
			return;
		}
		int value = 600;
		if (Main.rand.Next(value) != 0)
		{
			return;
		}
		int matrix = 3;
		int num = FindBuffIndex(207);
		if (num == -1)
		{
			matrix = 2;
			num = FindBuffIndex(206);
		}
		if (num == -1)
		{
			matrix = 1;
			num = FindBuffIndex(26);
		}
		if (num != -1)
		{
			int num2 = buffTime[num];
			DelBuff(num);
			int stack = Utils.Clamp(num2 / 3600 * matrix, matrix, 999);
			Vector2 mountedCenter = MountedCenter;
			ParticleOrchestrator.RequestParticleSpawn(clientOnly: false, ParticleOrchestraType.Digestion, new ParticleOrchestraSettings
			{
				PositionInWorld = mountedCenter,
				MovementVector = new Vector2(-direction, 0f)
			}, whoAmI);
			int num3 = Item.NewItem(GetItemSource_Misc(9), mountedCenter, Vector2.Zero, 5395, stack, noBroadcast: false, 0, noGrabDelay: true);
			if (Main.netMode == 0)
			{
				Main.item[num3].noGrabDelay = 100;
			}
			if (Main.netMode == 1)
			{
				NetMessage.SendData(21, -1, -1, null, num3);
			}
		}
	}

