	public void GiveImmuneTimeForCollisionAttack(int time)
	{
		if (_timeSinceLastImmuneGet <= 20)
		{
			_immuneStrikes++;
		}
		else
		{
			_immuneStrikes = 1;
		}
		_timeSinceLastImmuneGet = 0;
		if (_immuneStrikes < 3 && (!immune || immuneTime <= time))
		{
			immune = true;
			immuneNoBlink = true;
			immuneTime = time;
		}
	}

