			{
				if (velocity.Y > 0f)
				{
					velocity.Y -= 1f;
				}
				else if (velocity.Y > 0f - jumpSpeed)
				{
					velocity.Y -= 0.2f;
				}
				if (velocity.Y < (0f - jumpSpeed) * 3f)
				{
					velocity.Y = (0f - jumpSpeed) * 3f;
				}
			}
			else
			{
				if (velocity.Y < 0f)
				{
					velocity.Y += 1f;
				}
				else if (velocity.Y < jumpSpeed)
				{
					velocity.Y += 0.2f;
				}
