		}
		if (changeItem >= 0)
		{
			if (selectedItem != changeItem)
			{
				SoundEngine.PlaySound(12);
			}
			selectedItem = changeItem;
			changeItem = -1;
		}
		if (itemAnimation == 0 && selectedItem != 58)
		{
			while (selectedItem > 9)
			{
				selectedItem -= 10;
			}
			while (selectedItem < 0)
			{
				selectedItem += 10;
			}
		}
	}

	private static int ClampHotbarOffset(int Offset)
	{
		while (Offset > 9)
		{
			Offset -= 10;
		}
		while (Offset < 0)
		{
			Offset += 10;
		}
		return Offset;
	}

