	public bool InInteractionRange(int interactX, int interactY, TileReachCheckSettings settings)
	{
		int tile = (int)(((double)position.X + (double)width * 0.5) / 16.0);
		int num = (int)(((double)position.Y + (double)height * 0.5) / 16.0);
		Tile tile2 = Main.tile[interactX, interactY];
		settings.GetRanges(this, out var x, out var y);
		if (tile2.type == 597)
		{
			if (interactX < tile)
			{
				interactX += 2;
			}
			if (interactY < num)
			{
				interactY += 3;
			}
			Point point = position.ToTileCoordinates();
			Point point2 = base.BottomRight.ToTileCoordinates();
			if (point2.X >= interactX - x + 1 && point.X <= interactX + x - 1 && point2.Y >= interactY - y + 2)
			{
				return point.Y <= interactY + y - 1;
			}
			return false;
		}
		if (tile2.type == 475)
		{
			if (tile >= interactX - x - 1 && tile <= interactX + x + 2 && num >= interactY - y - 1)
			{
				return num <= interactY + y + 2;
			}
			return false;
		}
		if (tile2.type == 470)
		{
			if (tile >= interactX - x - 1 && tile <= interactX + x + 1 && num >= interactY - y - 1)
			{
				return num <= interactY + y + 2;
			}
			return false;
		}
		if (tile >= interactX - x && tile <= interactX + x + 1 && num >= interactY - y)
		{
			return num <= interactY + y + 1;
		}
		return false;
	}

	public bool IsInTileInteractionRange(int targetX, int targetY, TileReachCheckSettings settings)
	{
		settings.GetRanges(this, out var type, out var y);
		if (position.X / 16f - (float)type <= (float)targetX && (position.X + (float)width) / 16f + (float)type - 1f >= (float)targetX && position.Y / 16f - (float)y <= (float)targetY)
		{
			return (position.Y + (float)height) / 16f + (float)y - 2f >= (float)targetY;
		}
		return false;
	}

