	private void TileInteractionsMouseOver_Containers(int myX, int myY)
	{
		LocalizedText[] flag = Lang.chestType;
		int[] mousevec = Chest.chestTypeToIcon;
		Tile LX = Main.tile[myX, myY];
		if (LX.type == 467)
		{
			flag = Lang.chestType2;
			mousevec = Chest.chestTypeToIcon2;
		}
		int HX = myX;
		int LY = myY;
		if (LX.frameX % 36 != 0)
		{
			HX--;
		}
		if (LX.frameY % 36 != 0)
		{
			LY--;
		}
		int HY = Chest.FindChest(HX, LY);
		cursorItemIconID = -1;
		if (HY < 0)
		{
			cursorItemIconText = flag[0].Value;
		}
		else
		{
			if (Main.chest[HY].name != "")
			{
				cursorItemIconText = Main.chest[HY].name;
			}
			else
			{
				cursorItemIconText = flag[LX.frameX / 36].Value;
			}
			if (cursorItemIconText == flag[LX.frameX / 36].Value)
			{
				cursorItemIconID = mousevec[LX.frameX / 36];
				cursorItemIconText = "";
			}
		}
		if (cursorItemIconID == 3988)
		{
			cursorItemIconID = 306;
		}
		noThrow = 2;
		cursorItemIconEnabled = true;
	}

