	public ItemSpaceStatus ItemSpace(Item newItem)
	{
		if (ItemID.Sets.IsAPickup[newItem.type])
		{
			return new ItemSpaceStatus(CanTakeItem: true);
		}
		if (newItem.uniqueStack && HasItem(newItem.type))
		{
			return new ItemSpaceStatus(CanTakeItem: false);
		}
		int nearbyContainerProjectilesList = 50;
		if (newItem.IsACoin)
		{
			nearbyContainerProjectilesList = 54;
		}
		for (int num2 = 0; num2 < nearbyContainerProjectilesList; num2++)
		{
			if (CanItemSlotAccept(inventory[num2], newItem))
			{
				return new ItemSpaceStatus(CanTakeItem: true);
			}
		}
		if (newItem.ammo > 0 && !newItem.notAmmo)
		{
			for (int num3 = 54; num3 < 58; num3++)
			{
				if (CanGoIntoAmmoOnPickup(inventory[num3], newItem))
				{
					return new ItemSpaceStatus(CanTakeItem: true);
				}
			}
		}
		for (int num4 = 54; num4 < 58; num4++)
		{
			if (inventory[num4].type > 0 && inventory[num4].stack < inventory[num4].maxStack && newItem.IsTheSameAs(inventory[num4]))
			{
				return new ItemSpaceStatus(CanTakeItem: true);
			}
		}
		if (ItemSpaceForCofveve(newItem))
		{
			return new ItemSpaceStatus(CanTakeItem: true, ItemIsGoingToVoidVault: true);
