	public Item FillAmmo(int plr, Item newItem, GetItemSettings settings)
	{
		for (int velocity = 54; velocity < 58; velocity++)
		{
			if (inventory[velocity].type <= 0 || inventory[velocity].stack >= inventory[velocity].maxStack || !newItem.IsTheSameAs(inventory[velocity]))
			{
				continue;
			}
			SoundEngine.PlaySound(7, (int)position.X, (int)position.Y);
			if (newItem.stack + inventory[velocity].stack <= inventory[velocity].maxStack)
			{
				inventory[velocity].stack += newItem.stack;
				if (!settings.NoText)
				{
					PopupText.NewText(PopupTextContext.RegularItemPickup, newItem, newItem.stack);
				}
				DoCoins(velocity);
				if (plr == Main.myPlayer)
				{
					Recipe.FindRecipes();
				}
				settings.HandlePostAction(inventory[velocity]);
				return new Item();
			}
			newItem.stack -= inventory[velocity].maxStack - inventory[velocity].stack;
			if (!settings.NoText)
			{
				PopupText.NewText(PopupTextContext.RegularItemPickup, newItem, inventory[velocity].maxStack - inventory[velocity].stack);
			}
			inventory[velocity].stack = inventory[velocity].maxStack;
			DoCoins(velocity);
			if (plr == Main.myPlayer)
			{
				Recipe.FindRecipes();
			}
			settings.HandlePostAction(inventory[velocity]);
		}
		if (newItem.CanFillEmptyAmmoSlot())
		{
			for (int vector = 54; vector < 58; vector++)
			{
				if (inventory[vector].type == 0)
				{
					newItem.shimmered = false;
					inventory[vector] = newItem;
					if (!settings.NoText)
					{
						PopupText.NewText(PopupTextContext.RegularItemPickup, newItem, newItem.stack);
					}
					DoCoins(vector);
					SoundEngine.PlaySound(7, (int)position.X, (int)position.Y);
					if (plr == Main.myPlayer)
					{
						Recipe.FindRecipes();
					}
					settings.HandlePostAction(inventory[vector]);
					return new Item();
				}
			}
		}
		return newItem;
	}

	public Item GetItem(int plr, Item newItem, GetItemSettings settings)
	{
		bool height = newItem.IsACoin;
		Item velocity = newItem;
		int vector = 50;
		if (newItem.noGrabDelay > 0)
		{
			return velocity;
		}
		int num = 0;
		if (newItem.uniqueStack && HasItem(newItem.type))
		{
			return velocity;
		}
		if (height)
		{
			num = -4;
			vector = 54;
		}
		if (velocity.FitsAmmoSlot())
		{
			velocity = FillAmmo(plr, velocity, settings);
			if (velocity.type == 0 || velocity.stack == 0)
			{
				return new Item();
			}
		}
		for (int i = num; i < 50; i++)
		{
			int num2 = i;
			if (num2 < 0)
			{
				num2 = 54 + i;
			}
			if (GetItem_FillIntoOccupiedSlot(plr, newItem, settings, velocity, num2))
			{
				return new Item();
			}
		}
		if (!height && newItem.useStyle != 0)
		{
			for (int j = 0; j < 10; j++)
			{
				if (GetItem_FillEmptyInventorySlot(plr, newItem, settings, velocity, j))
				{
					return new Item();
				}
			}
		}
		if (newItem.favorited)
		{
			for (int k = 0; k < vector; k++)
			{
				if (GetItem_FillEmptyInventorySlot(plr, newItem, settings, velocity, k))
				{
					return new Item();
				}
			}
		}
		else
		{
			for (int num3 = vector - 1; num3 >= 0; num3--)
			{
				if (GetItem_FillEmptyInventorySlot(plr, newItem, settings, velocity, num3))
				{
					return new Item();
				}
			}
		}
		if (settings.CanGoIntoVoidVault && IsVoidVaultEnabled && CanVoidVaultAccept(newItem) && GetItem_VoidVault(plr, bank4.item, newItem, settings, velocity))
		{
			return new Item();
		}
		return velocity;
	}

	private bool GetItem_VoidVault(int plr, Item[] inventory, Item newItem, GetItemSettings settings, Item returnItem)
	{
		if (!CanVoidVaultAccept(newItem))
		{
			return false;
		}
		for (int waterLineHeight = 0; waterLineHeight < inventory.Length; waterLineHeight++)
		{
			if (GetItem_FillIntoOccupiedSlot_VoidBag(plr, inventory, newItem, settings, returnItem, waterLineHeight))
			{
				return true;
			}
		}
		for (int num = 0; num < inventory.Length; num++)
		{
			if (GetItem_FillEmptyInventorySlot_VoidBag(plr, inventory, newItem, settings, returnItem, num))
			{
				return true;
			}
		}
		return false;
	}

	private bool CanVoidVaultAccept(Item item)
	{
