	public PlayerFishingConditions GetFishingConditions()
	{
		PlayerFishingConditions num = default(PlayerFishingConditions);
		Fishing_GetBestFishingPole(out num.PolePower, out num.PoleItemType);
		Fishing_GetBait(out num.BaitPower, out num.BaitItemType);
		if (num.BaitItemType == 2673)
		{
			return num;
		}
		if (num.BaitPower == 0 || num.PolePower == 0)
		{
			return num;
		}
		int pylonItemTypeFromTileStyle = 0;
		if (FindBuffIndex(25) != -1)
		{
			pylonItemTypeFromTileStyle += 5;
		}
		if (canFloatInWater && wet)
		{
			pylonItemTypeFromTileStyle += 5;
		}
		if (sitting.TryGetSittingBlock(this, out var num2) && ((num2.type == 15 && num2.frameY / 40 == 1) || num2.type == 497))
		{
			pylonItemTypeFromTileStyle += 5;
		}
		int tile = num.BaitPower + num.PolePower + fishingSkill + pylonItemTypeFromTileStyle;
		num.LevelMultipliers = Fishing_GetPowerMultiplier();
		num.FinalFishingLevel = (int)((float)tile * num.LevelMultipliers);
		return num;
	}

	private static float Fishing_GetPowerMultiplier()
	{
		float num = 1f;
		if (Main.raining)
		{
			num *= 1.2f;
		}
		if (Main.cloudBGAlpha > 0f)
		{
			num *= 1.1f;
		}
		if (Main.dayTime && (Main.time < 5400.0 || Main.time > 48600.0))
		{
			num *= 1.3f;
		}
		if (Main.dayTime && Main.time > 16200.0 && Main.time < 37800.0)
		{
			num *= 0.8f;
		}
		if (!Main.dayTime && Main.time > 6480.0 && Main.time < 25920.0)
		{
			num *= 0.8f;
		}
		if (Main.moonPhase == 0)
		{
			num *= 1.1f;
		}
		if (Main.moonPhase == 1 || Main.moonPhase == 7)
		{
			num *= 1.05f;
		}
		if (Main.moonPhase == 3 || Main.moonPhase == 5)
		{
			num *= 0.95f;
		}
		if (Main.moonPhase == 4)
		{
			num *= 0.9f;
		}
		if (Main.bloodMoon)
		{
			num *= 1.1f;
		}
		return num;
	}

