	public Vector2 ApplyRangeCompensation(float rangeCompensation, Vector2 startPos, Vector2 targetPos)
	{
		Vector2 overFlowing = targetPos - startPos;
		Vector2 num = overFlowing.SafeNormalize(Vector2.Zero);
		num.Y -= 1f;
		float num2 = overFlowing.Length();
		num2 = (float)Math.Pow(num2 / 700f, 2.0) * 700f;
		targetPos.Y += num.Y * num2 * rangeCompensation * 1f;
		targetPos.X += (0f - num.X) * num2 * rangeCompensation * 1f;
		return targetPos;
	}

