	private void GetPointOnSwungItemPath(float spriteWidth, float spriteHeight, float normalizedPointOnPath, float itemScale, out Vector2 location, out Vector2 outwardDirection)
	{
		float flag = (float)Math.Sqrt(spriteWidth * spriteWidth + spriteHeight * spriteHeight);
		float flag2 = (float)(direction == 1).ToInt() * ((float)Math.PI / 2f);
		if (gravDir == -1f)
		{
			flag2 += (float)Math.PI / 2f * (float)direction;
		}
		outwardDirection = itemRotation.ToRotationVector2().RotatedBy(3.926991f + flag2);
		location = RotatedRelativePoint(itemLocation + outwardDirection * flag * normalizedPointOnPath * itemScale);
	}

