	private Point FindSharpTearsSpot(Vector2 targetSpot)
	{
		Point i = targetSpot.ToTileCoordinates();
		Vector2 j = base.Center;
		Vector2 endPoint = targetSpot;
		int samplesToTake = 3;
		float samplingWidth = 4f;
		Collision.AimingLaserScan(j, endPoint, samplingWidth, samplesToTake, out var vectorTowardsTarget, out var samples);
		float num = float.PositiveInfinity;
		for (int k = 0; k < samples.Length; k++)
		{
			if (samples[k] < num)
			{
				num = samples[k];
			}
		}
		targetSpot = j + vectorTowardsTarget.SafeNormalize(Vector2.Zero) * num;
		i = targetSpot.ToTileCoordinates();
		Rectangle value = new Rectangle(i.X, i.Y, 1, 1);
		value.Inflate(6, 16);
		Rectangle value2 = new Rectangle(0, 0, Main.maxTilesX, Main.maxTilesY);
		value2.Inflate(-40, -40);
		value = Rectangle.Intersect(value, value2);
		List<Point> list = new List<Point>();
		List<Point> list2 = new List<Point>();
		for (int l = value.Left; l <= value.Right; l++)
		{
			for (int m = value.Top; m <= value.Bottom; m++)
			{
				if (!WorldGen.SolidTile2(l, m))
				{
					continue;
				}
				Vector2 value3 = new Vector2(l * 16 + 8, m * 16 + 8);
				if (!(Vector2.Distance(targetSpot, value3) > 200f))
				{
					if (FindSharpTearsOpening(l, m, l > i.X, l < i.X, m > i.Y, m < i.Y))
					{
						list.Add(new Point(l, m));
					}
					else
					{
						list2.Add(new Point(l, m));
					}
				}
			}
		}
		if (list.Count == 0 && list2.Count == 0)
		{
			list.Add((base.Center.ToTileCoordinates().ToVector2() + Main.rand.NextVector2Square(-2f, 2f)).ToPoint());
		}
		List<Point> list3 = list;
		if (list3.Count == 0)
		{
			list3 = list2;
		}
		int index = Main.rand.Next(list3.Count);
		return list3[index];
	}

	private bool FindSharpTearsOpening(int x, int y, bool acceptLeft, bool acceptRight, bool acceptUp, bool acceptDown)
	{
		if (acceptLeft && !WorldGen.SolidTile(x - 1, y))
		{
			return true;
		}
		if (acceptRight && !WorldGen.SolidTile(x + 1, y))
		{
			return true;
		}
		if (acceptUp && !WorldGen.SolidTile(x, y - 1))
		{
			return true;
		}
		if (acceptDown && !WorldGen.SolidTile(x, y + 1))
		{
			return true;
		}
		return false;
	}

