	public void UpdateMaxTurrets()
	{
		if (Main.myPlayer != whoAmI)
		{
			return;
		}
		List<Projectile> array = new List<Projectile>();
		for (int i = 0; i < 1000; i++)
		{
			if (Main.projectile[i].WipableTurret)
			{
				array.Add(Main.projectile[i]);
			}
		}
		int spinningpoint = 0;
		while (array.Count > maxTurrets && ++spinningpoint < 1000)
		{
			Projectile projectile = array[0];
			for (int j = 1; j < array.Count; j++)
			{
				if (array[j].timeLeft < projectile.timeLeft)
				{
					projectile = array[j];
				}
			}
			projectile.Kill();
			array.Remove(projectile);
		}
	}

