				if (sItem.type == 1156 || sItem.type == 3350 || sItem.type == 3210 || sItem.type == 160 || sItem.type == 3821)
				{
					num2 = specialistEffectiveDamage;
				}
				num = (int)((float)num * num2 + 5E-06f);
