				flag.bank.item[tileId] = bank.item[tileId].Clone();
			}
			if (tileId < bank2.item.Length)
			{
				flag.bank2.item[tileId] = bank2.item[tileId].Clone();
			}
			if (tileId < bank3.item.Length)
			{
				flag.bank3.item[tileId] = bank3.item[tileId].Clone();
			}
			if (tileId < bank4.item.Length)
			{
				flag.bank4.item[tileId] = bank4.item[tileId].Clone();
			}
		}
		CloneLoadouts(flag);
		flag.trashItem = trashItem.Clone();
		for (int num = 0; num < maxBuffs; num++)
		{
			flag.buffType[num] = buffType[num];
			flag.buffTime[num] = buffTime[num];
		}
		DpadRadial.CopyTo(flag.DpadRadial);
		CircularRadial.CopyTo(flag.CircularRadial);
