		string num = playerFile.Path;
		Player num2 = playerFile.Player;
		bool isCloudSave = playerFile.IsCloudSave;
		if (string.IsNullOrEmpty(num))
		{
			return;
		}
		if (FileUtilities.Exists(num, isCloudSave))
		{
			FileUtilities.Copy(num, num + ".bak", isCloudSave);
		}
		RijndaelManaged rijndaelManaged = new RijndaelManaged();
		using Stream stream = (isCloudSave ? ((Stream)new MemoryStream(2000)) : ((Stream)new FileStream(num, FileMode.Create)));
		using CryptoStream cryptoStream = new CryptoStream(stream, rijndaelManaged.CreateEncryptor(ENCRYPTION_KEY, ENCRYPTION_KEY), CryptoStreamMode.Write);
		using BinaryWriter binaryWriter = new BinaryWriter(cryptoStream);
		binaryWriter.Write(279);
		playerFile.Metadata.Write(binaryWriter);
		Serialize(playerFile, num2, binaryWriter);
		binaryWriter.Flush();
		cryptoStream.FlushFinalBlock();
		stream.Flush();
