		if (Main.rand == null)
		{
			Main.rand = new UnifiedRandom((int)DateTime.Now.Ticks);
		}
		Player center = new Player();
		bool endPoint = false;
		try
		{
			RijndaelManaged vectorTowardsTarget = new RijndaelManaged();
			vectorTowardsTarget.Padding = PaddingMode.None;
			using (MemoryStream samples = new MemoryStream(FileUtilities.ReadAllBytes(playerPath, cloudSave)))
			{
				using CryptoStream num = new CryptoStream(samples, vectorTowardsTarget.CreateDecryptor(ENCRYPTION_KEY, ENCRYPTION_KEY), CryptoStreamMode.Read);
				using BinaryReader value = new BinaryReader(num);
				int value2 = value.ReadInt32();
				if (value2 >= 135)
				{
					point.Metadata = FileMetadata.Read(value, FileType.Player);
				}
				else
				{
					point.Metadata = FileMetadata.FromCurrentSettings(FileType.Player);
				}
				if (value2 > 279)
				{
					center.loadStatus = 1;
					center.name = value.ReadString();
					point.Player = center;
					return point;
				}
				Deserialize(point, center, value, value2, out endPoint);
			}
			center.PlayerFrame();
			center.loadStatus = 0;
			point.Player = center;
			return point;
		}
		catch
		{
		}
		Player samplesToTake = new Player();
		samplesToTake.loadStatus = 2;
		if (endPoint && center.name.Length <= nameLen)
		{
			samplesToTake.name = center.name;
		}
