			string[] list2 = playerPath.Replace('/', Path.DirectorySeparatorChar).Split(Path.DirectorySeparatorChar);
			samplesToTake.name = list2[list2.Length - 1].Split('.')[0];
		}
		point.Player = samplesToTake;
		return point;
	}

