	public Vector2 CheckForGoodTeleportationSpot(ref bool canSpawn, int teleportStartX, int teleportRangeX, int teleportStartY, int teleportRangeY, RandomTeleportationAttemptSettings settings)
	{
		int flag = 0;
		int flag2 = 0;
		int num = 0;
		int num2 = width;
		Vector2 vector = new Vector2(flag2, num) * 16f + new Vector2(-num2 / 2 + 8, -height);
		while (!canSpawn && flag < settings.attemptsBeforeGivingUp)
		{
			flag++;
			flag2 = teleportStartX + Main.rand.Next(teleportRangeX);
			num = teleportStartY + Main.rand.Next(teleportRangeY);
			int num3 = 5;
			flag2 = (int)MathHelper.Clamp(flag2, num3, Main.maxTilesX - num3);
			num = (int)MathHelper.Clamp(num, num3, Main.maxTilesY - num3);
			vector = new Vector2(flag2, num) * 16f + new Vector2(-num2 / 2 + 8, -height);
			if (Collision.SolidCollision(vector, num2, height))
			{
				continue;
			}
			if (Main.tile[flag2, num] == null)
			{
				Main.tile[flag2, num] = new Tile();
			}
			if ((settings.avoidWalls && Main.tile[flag2, num].wall > 0) || (Main.tile[flag2, num].wall == 87 && (double)num > Main.worldSurface && !NPC.downedPlantBoss) || (Main.wallDungeon[Main.tile[flag2, num].wall] && (double)num > Main.worldSurface && !NPC.downedBoss3))
			{
				continue;
			}
			int num4 = 0;
			while (num4 < settings.maximumFallDistanceFromOrignalPoint)
			{
				if (Main.tile[flag2, num + num4] == null)
				{
					Main.tile[flag2, num + num4] = new Tile();
				}
				Tile tile = Main.tile[flag2, num + num4];
				vector = new Vector2(flag2, num + num4) * 16f + new Vector2(-num2 / 2 + 8, -height);
				Collision.SlopeCollision(vector, velocity, num2, height, gravDir);
				if (!Collision.SolidCollision(vector, num2, height))
				{
					num4++;
					continue;
				}
				if (tile.active() && !tile.inActive() && Main.tileSolid[tile.type])
				{
					break;
				}
				num4++;
			}
			vector.Y -= 16f;
			int i = (int)vector.X / 16;
			int j = (int)vector.Y / 16;
			int num5 = (int)(vector.X + (float)num2 * 0.5f) / 16;
			int j2 = (int)(vector.Y + (float)height) / 16;
			Tile tileSafely = Framing.GetTileSafely(i, j);
			Tile tileSafely2 = Framing.GetTileSafely(num5, j2);
			if (settings.avoidAnyLiquid && tileSafely2.liquid > 0)
			{
				continue;
			}
			if (settings.mostlySolidFloor)
			{
				Tile tileSafely3 = Framing.GetTileSafely(num5 - 1, j2);
				Tile tileSafely4 = Framing.GetTileSafely(num5 + 1, j2);
				if (!tileSafely3.active() || tileSafely3.inActive() || !Main.tileSolid[tileSafely3.type] || Main.tileSolidTop[tileSafely3.type] || !tileSafely4.active() || tileSafely4.inActive() || !Main.tileSolid[tileSafely4.type] || Main.tileSolidTop[tileSafely4.type])
				{
					continue;
				}
			}
			if ((settings.avoidWalls && tileSafely.wall > 0) || (settings.avoidAnyLiquid && Collision.WetCollision(vector, num2, height)) || (settings.avoidLava && Collision.LavaCollision(vector, num2, height)) || (settings.avoidHurtTiles && Collision.AnyHurtingTiles(vector, num2, height)) || Collision.SolidCollision(vector, num2, height) || num4 >= settings.maximumFallDistanceFromOrignalPoint - 1)
			{
				continue;
			}
			Vector2 vector2 = Vector2.UnitX * 16f;
			if (Collision.TileCollision(vector - vector2, vector2, width, height, fallThrough: false, fall2: false, (int)gravDir) != vector2)
			{
				continue;
			}
			vector2 = -Vector2.UnitX * 16f;
			if (Collision.TileCollision(vector - vector2, vector2, width, height, fallThrough: false, fall2: false, (int)gravDir) != vector2)
			{
				continue;
			}
			vector2 = Vector2.UnitY * 16f;
			if (!(Collision.TileCollision(vector - vector2, vector2, width, height, fallThrough: false, fall2: false, (int)gravDir) != vector2))
			{
				vector2 = -Vector2.UnitY * 16f;
				if (!(Collision.TileCollision(vector - vector2, vector2, width, height, fallThrough: false, fall2: false, (int)gravDir) != vector2))
				{
					canSpawn = true;
					num += num4;
					break;
				}
			}
		}
		return vector;
	}

