	public bool CheckMana(int amount, bool pay = false, bool blockQuickMana = false)
	{
		int list = (int)((float)amount * manaCost);
		if (statMana >= list)
		{
			if (pay)
			{
				statMana -= list;
			}
			return true;
		}
		if (manaFlower && !blockQuickMana)
		{
			QuickMana();
			if (statMana >= list)
			{
				if (pay)
				{
					statMana -= list;
				}
				return true;
			}
			return false;
		}
		return false;
	}

